import React from 'react'
import {Card, CardMedia, CardContent, CardActions, Typography, IconButton} from '@material-ui/core'
import {AddShoppingCart} from '@material-ui/icons'
import useStyles from "./styles"
import { useState } from 'react';
import { FaArrowAltCircleRight, FaArrowAltCircleLeft } from 'react-icons/fa';


const Product = ({ product, onAddToCart }) => {
    const classes = useStyles();

    function buttonHandle() {
        onAddToCart(product.id,1);
        window.location.reload(false);
      }

      const slides = product.assets

function ImageSlider(){
  const [current, setCurrent] = useState(0);
  const length = slides.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }

  return (
    <section className='slider'>
      <FaArrowAltCircleLeft className='left-arrow' onClick={prevSlide} />
      <FaArrowAltCircleRight className='right-arrow' onClick={nextSlide} />
      {slides.map((slide, index) => {
        return (
          <div
            className={index === current ? 'slide active' : 'slide'}
            key={index}
          >
            {index === current && (
              <img src={slide.image} alt='travel image' className='image' />
            )}
          </div>
        );
      })}
    </section>
  );
};

      function displayProductImgs(){

          <ImageSlider/>

      }



    return (
        <Card className={classes.root} >
            <CardMedia className={classes.media} image={product.media.source} title={product.name}  onClick={() => displayProductImgs()}/>

            <CardContent>
                <div className={classes.cardContent}>

                    <Typography variant='h5' gutterBottom>
                        {product.name}
                    </Typography>

                    <Typography variant='h5'>
                        {product.price.formatted_with_symbol}
                    </Typography>


                </div>

                <Typography dangerouslySetInnerHTML={{__html: product.description}}  variant='body2' style={{backgroundColor:'black', opacity:'25%'}}/>

            </CardContent>

            <CardActions disableSpacing className={classes.cardActions}>
                <IconButton aria-label='Add to Cart' onClick={() => buttonHandle()}>
                    <AddShoppingCart/>

                </IconButton>
            </CardActions>
            </Card>
    )
}

export default Product
