import React from 'react'
import '../../App.css';
import Products from '../Product/Products'
import {commerce} from './lib/commerce.js';


function Shop() {

    const [products,setProducts] = useState([]);

    const fetchProducts = async () => {
        const { data } = await commerce.products.list();

        setProducts(data);
    }

    useEffect(() => {
        fetchProducts();
    }, []);

    return (
        <>
        <Products/>

        </>
    )
}

export default Shop
